import React from 'react'
import { Menu, Skeleton } from 'antd'
import {
  SnippetsFilled,
  HeartFilled,
  SignalFilled
} from '@ant-design/icons'
import Link from 'next/link'
import Router from 'next/router'
import styles from './home.less'

class Home extends React.Component {
  constructor(props) {
    super(props)
    const { title } = props
    this.state = {
      title,
      current: '',
    }
  }

  handleClick = (e) => {
    Router.push(e.key)
    this.setState({
      current: e.key,
    })
  }

  render() {
    return (
      <div className={'home-header'}>
        <div className={'content'}>
          <div className={'title'}>
            <Link href="/">
              <span className="sys-name">包崽无限公司</span>
            </Link>
          </div>
          <Menu
            onClick={this.handleClick}
            selectedKeys={[this.state.current]}
            mode="horizontal"
          >
            <Menu.Item key="/blog">
              <SnippetsFilled twoToneColor="#ffcccc" />
              Blog
            </Menu.Item>

            <Menu.Item key="/cat">
              <HeartFilled twoToneColor="#ffcccc" />
              Cat
            </Menu.Item>

            <Menu.Item key="/life">
              <SignalFilled twoToneColor="#ffcccc" />
              Life
            </Menu.Item>
          </Menu>
          <Skeleton active />
        </div>
      </div>
    )
  }
}

export default Home
